import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllCities } from "../actions/citiesAction";
import { Link } from "react-router-dom";
import ToggleDisplay from "react-toggle-display";
import Itineraries from "./Itinerary";

class AllCities extends Component {
  constructor() {
    super();
    this.state = {
      search: "",
      show: false
    };
  }

  //methode to make the filter search
  updateSearch(event) {
    // console.log(event.target.value);
    this.setState({ search: event.target.value });
  }

  componentDidMount() {
    this.props.getAllCities();
    this.props.handleClick();
  }

  handleClick() {
    this.setState({
      show: !this.state.show
    });
  }

  render() {
    //added for earch bar
    let filteredCities = this.props.cities.filter(theCity => {
      return (
        theCity.city.toUpperCase().indexOf(this.state.search.toUpperCase()) !==
        -1
      );
    });
    console.log(this.props);

    return (
      <div className="AllCities">
        <input
          type="text"
          value={this.state.search}
          onChange={this.updateSearch.bind(this)}
        />

        {/* <h1>this is the page for all cities!!!</h1> */}

        <div>
          {filteredCities.map(city => (
            <Link to={`/itineraries/${city.city}`}>
              <p
                className="cityPage"
                key={city.city}
                onClick={() => this.handleClick()}
              >
                {city.city}
              </p>
            </Link>
          ))}
        </div>
        <ToggleDisplay if={this.state.show} tag="section">
          {" "}
          <Itineraries />{" "}
        </ToggleDisplay>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    cities: state.cities
  };
};

export default connect(
  mapStateToProps,
  { getAllCities }
)(AllCities);
