import axios from "axios";

export function getActivities() {
  return dispatch => {
    axios.get(`/api/activities`).then(res => {
      dispatch(activityInfo(res.data));
    });
  };
}

export function activityInfo(activity) {
  return {
    type: "ALL_ACTIVITY",
    activities: activity
  };
}

export function getActivitiesByTitle(actvitytitle) {
  return dispatch => {
    axios.get(`/api/activities/${actvitytitle}`).then(res => {
      dispatch(activitySpecific(res.data));
    });
  };
}

export function activitySpecific(actvitytitle) {
  return {
    type: "ADD_SPECIFICACTIVITY",
    activities: actvitytitle
  };
}
