  componentDidMount() {
    axios
      .get(`/api/users/currentUser`, {
        withCredentials: true,
        headers: { Authorization: localStorage.getItem("token") }
      })
      .then(res => {
        console.log(res);
        console.log(res.data[0]._id);
        this.setState({ currentUserId: res.data[0]._id });
