import React, { Component } from "react";
// import axios from "axios";
import { connect } from "react-redux";

import { getAllCities } from "../actions/citiesAction";

class AllCities extends Component {
  // state = {
  //   cities: []
  // };

  // componentDidMount() {
  //   //make sure you dont write the link like this :http://localhost:5000/api/cities (local host is not necessary)
  //   axios.get(`/api/cities`).then(res => {
  //     console.log(res);
  //     this.setState({ cities: res.data });
  //   });
  // }

  render() {
    console.log(this.props);

    return (
      <div className="AllCities">
        {/* <h1>this is the page for all cities!!!</h1> */}

        <div>
          {this.props.cities.map(city => (
            <p className="cityPage" key={city.city}>
              {city.city}
            </p>
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    cities: state.cities
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getAllCities: () => {
      dispatch(getAllCities());
    }
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AllCities);
