import React, { Component } from "react";

import axios from "axios";
import Slider from "react-animated-slider";
import "react-animated-slider/build/horizontal.css";

class Activities extends Component {
  state = {
    images: [
      {
        dec: "casa batllio",
        img:
          "https://www.dosde.com/media/catalog/product/cache/2/image/9df78eab33525d08d6e5fb8d27136e95/c/a/casa-batllo-gaudi-english-book-dosde-publishing_978-84-15818-05-2_20-013-01_B02.jpg"
      },
      {
        dec: "casa mila",
        img:
          "https://images.adsttc.com/media/images/517e/a80b/b3fc/4b0c/e700/000a/newsletter/antoni-gaudi-spain-barcelona-casa-mila-01-samuel-ludwig.jpg?1367255048"
      },
      {
        dec: "gothic",
        img:
          "https://cache-graphicslib.viator.com/graphicslib/thumbs674x446/7548/SITours/picasso-museum-and-gothic-quarter-walking-tour-in-barcelona-in-barcelona-336445.jpg"
      },
      {
        dec: "tapas bar",
        img:
          "https://barcelona-home.com/blog/wp-content/upload/2013/12/table-full-of-tapas-955x508.jpg"
      },
      {
        dec: "segrada famila",
        img:
          "https://www.livingtours.com/media/catalog/product/cache/2/image/830x545/17f82f742ffe127f42dca9de82fb58b1/s/a/sagrada-familia-tour_2.jpg"
      }
    ],
    isVisible: false
  };

  componentWillReceiveProps(nextProps) {
    console.log(nextProps.isVisible);
    this.setState({ isVisible: nextProps.isVisible });
  }

  componentDidMount() {
    //make sure you dont write the link like this :http://localhost:5000/api/cities (local host is not necessary)
    axios.get(`/api/activities`).then(res => {
      console.log(res.data[0][id]);
      // this.setState({ images: res.data });
    });
  }

  render() {
    return (
      <div style={{ display: this.state.isVisible ? "inline" : "none" }}>
        <Slider>
          {this.state.images.map((image, index) => (
            <div key={index}>
              <div>
                <h2>{image.dec}</h2>
                <img src={image.img} />
              </div>
            </div>
          ))}
        </Slider>
      </div>
    );
  }
}

export default Activities;
