import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllCities } from "../actions/citiesAction";
import { Link } from "react-router-dom";
import Axios from "axios";

class CityAdmin extends Component {
  constructor() {
    super();
    this.state = {
      search: ""
    };
  }

  //methode to make the filter search
  updateSearch(event) {
    // console.log(event.target.value);
    this.setState({ search: event.target.value });
  }

  componentDidMount() {
    console.log("1");
    this.props.getAllCities();
  }

  toRemove = e => {
    e.persist();
    console.log(e.target.id);
    const cityToDelete = e.target.id;

    Axios.delete(`/api/cities/${cityToDelete}`).then(
      console.log("was deleted")
    );
  };

  render() {
    //added for earch bar
    console.log(this.props.cities);
    let filteredCities = this.props.cities.filter(theCity => {
      return (
        theCity.city.toUpperCase().indexOf(this.state.search.toUpperCase()) !==
        -1
      );
    });
    console.log("4");

    return (
      <div className="AllCities">
        <input
          type="text"
          value={this.state.search}
          onChange={this.updateSearch.bind(this)}
        />

        {/* <h1>this is the page for all cities!!!</h1> */}

        <div>
          {filteredCities.map(city => (
            <p className="cityPage" key={city._id}>
              {city.city}
              <span>
                <button onClick={this.toRemove} id={city._id}>
                  {" "}
                  X{" "}
                </button>
              </span>
            </p>
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    cities: state.cities
  };
};

export default connect(
  mapStateToProps,
  { getAllCities }
)(CityAdmin);
