    console.log(this.props.users.favouriteItineraries);
    return (
      <div className="Favourites">
        my favorites page
        {this.props.users.favouriteItineraries.map((favoriteID, index) => (
          <GetFavorit favItineraryIDs={favoriteID} />
        ))}
      </div>
    );
  }
}

const mapStateToProps = state => {
