import React, { Component } from "react";
import { connect } from "react-redux";
import { getActivitiesByTitle } from "../actions/activitiesAction";
import axios from "axios";
import Slider from "react-animated-slider";
import "react-animated-slider/build/horizontal.css";

class Activities extends Component {
  state = {
    activities: [],
    isVisible: false
  };

  componentWillReceiveProps(nextProps) {
    console.log(nextProps.isVisible);
    this.setState({ isVisible: nextProps.isVisible });
  }

  componentDidMount(props) {
    //make sure you dont write the link like this :http://localhost:5000/api/cities (local host is not necessary)
    // axios.get(`/api/activities`).then(res => {
    //   console.log(res);
    //   this.setState({ activities: res.data });
    // });
    if (this.props.match.params.activitytitle) {
      this.props.getActivitiesByTitle(this.props.match.params.activitytitle);
    }
  }

  render() {
    return (
      <div style={{ display: this.state.isVisible ? "inline" : "none" }}>
        <Slider>
          {this.props.activities.map((activity, index) => (
            <div key={index}>
              <div>
                <h2>{activity.title}</h2>
                <img src={activity.image} />
              </div>
            </div>
          ))}
        </Slider>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    activities: state.activities
  };
};

export default connect(
  mapStateToProps,
  { getActivitiesByTitle }
)(Activities);
