import React, { Component } from "react";
import Favourites from "./Favourites";
import { Link } from "react-router-dom";
import { getCurrentUser } from "../actions/currentUserAction";
import { connect } from "react-redux";
import axios from "axios";

class Profile extends Component {
  state = {
    detail: {
      id: "5c18c5f8288fef047d9cc9e8",
      name: "shadan",
      password: "12345"
    }
  };

  componentDidMount() {
    this.props.getCurrentUser();
  }

  updateDetail = () => {
    console.log("yes clicked");
    const detail = {
      id: this.state.id,
      name: this.state.name,
      password: this.state.password
    };
    axios
      .post(`/api/users/updateDetails/${this.state.detail.id}`, { detail })
      .then(res => console.log(res));
  };

  render() {
    console.log(this.props.users);
    return (
      <div className="Profile">
        <p>welcome to your profile </p>
        <button onClick={this.updateDetail}>upadate meeeee</button>
        <div>
          <p>Name :{this.props.users.name}</p>
          <p>email :{this.props.users.email}</p>
        </div>

        <Link to="/fav">my likes</Link>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    users: state.users
  };
};
export default connect(
  mapStateToProps,
  { getCurrentUser }
)(Profile);
