import React, { Component } from "react";
import axios from "axios";
import { getCurrentUser } from "../actions/currentUserAction";
import { connect } from "react-redux";
import GetFavorit from "./GetFavorit";

class Favourites extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentDidMount() {
    // console.log("1");
    // axios
    //   .get(`/api/users/currentUser`, {
    //     withCredentials: true,
    //     headers: { Authorization: localStorage.getItem("token") }
    //   })
    //   .then(res => {
    //     console.log(res);

    //     this.setState({ allOfThem: res.data[0].favouriteItineraries });
    //   });

    this.props.getCurrentUser();
  }

  componentWillReceiveProps(nextProps) {
    const favIds = nextProps.users.favouriteItineraries;
    console.log(favIds);
    axios
      .post(`/api/itineraries/fav`, { favIds })
      .then(res => console.log(res));
  }

  render() {
    console.log(this.props.users.favouriteItineraries);
    return (
      <div className="Favourites">
        {/* my favorites page
        {this.props.users.map(function(favouriteItineraries, i) {
          <GetFavorit id={favouriteItineraries.favouriteItineraries} />;
        })} */}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    users: state.users
  };
};
export default connect(
  mapStateToProps,
  { getCurrentUser }
)(Favourites);

