import React, { Component } from "react";
import axios from "axios";

class Favourites extends Component {
  constructor(props) {
    super(props);
    this.state = {
      allOfThem: null,
      man: null
    };
  }

  componentDidMount() {
    console.log("1");
    axios
      .get(`/api/users/currentUser`, {
        withCredentials: true,
        headers: { Authorization: localStorage.getItem("token") }
      })
      .then(res => {
        console.log(res);

        this.setState({ allOfThem: res.data[0].favouriteItineraries });
      });
  }

  componentWillReceiveProps() {
    const favIds = this.state.allOfThem;
    axios
      .post(`/api/itineraries/fav`, { favIds })
      .then(res => this.setState({ man: res.data }));
  }

  render() {
    console.log(this.state.allOfThem);
    console.log(this.state.man);

    return <div className="Favourites">my favorites page</div>;
  }
}

export default Favourites;
