import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllCities } from "../actions/citiesAction";

class AllCities extends Component {
  constructor() {
    super();
    this.state = {
      search: "type in a city ..."
    };
  }

  updateSearch(event) {
    // console.log(event.target.value);
    this.setState({ search: event.target.value });
  }

  componentDidMount() {
    this.props.getAllCities();
  }

  render() {
    let filteredCities =
      this.props.cities.filter(filteredCity =>
        filteredCity.city.indexOf(this.state.search)
      ) !== -1;
    console.log(this.props);

    return (
      <div className="AllCities">
        <input
          type="text"
          value={this.state.search}
          onChange={this.updateSearch.bind(this)}
        />

        {/* <h1>this is the page for all cities!!!</h1> */}

        <div>
          {filteredCities.map(city => (
            <p className="cityPage" key={city.city}>
              {city.city}
            </p>
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    cities: state.cities
  };
};

export default connect(
  mapStateToProps,
  { getAllCities }
)(AllCities);
