import React, { Component } from "react";
import axios from "axios";

class Admin extends Component {
  state = {
    city: null,
    country: null
  };

  handleChange = e => {
    this.setState({
      city: this.refs.newCity.value,
      country: this.refs.newCountry.value
    });
  };

  handleSubmit = e => {
    e.preventDefault();

    const newCity = {
      city: this.state.city,
      country: this.state.country
    };

    this.refs.newCity.value = "";
    this.refs.newCountry.value = "";

    console.log(newCity);

    axios
      .post(
        `/api/cities`,
        { newCity },
        {
          withCredentials: true,
          headers: { Authorization: localStorage.getItem("token") }
        }
      )
      .then(res => {
        console.log(res);
        console.log(res.data);
      });
  };

  render() {
    return (
      <div className="Admin">
        <div>city 1. add 2. remove 3. edit</div>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            required
            ref="newCity"
            placeholder="add a new city"
            onChange={this.handleChange}
          />
          <input
            type="text"
            required
            ref="newCountry"
            placeholder="add a new country"
            onChange={this.handleChange}
          />
          <input type="submit" />
        </form>

        {/* <div>activity 1.add 2.remove 3. edit</div>
        <div>itinerary 1.add 2.remove 3.edit</div> */}
      </div>
    );
  }
}

export default Admin;
