const initState = {
  cities: [
    {
      country: "Italy",
      city: "Milan"
    }
  ],
  itineraries: null
};

const rootReducer = (state = initState, action) => {
  if (action.type === "ADD_City") {
    return {
      ...state,
      cities: action.cities
    };
  }
  if (action.type === "ALL_Itinerary") {
    return {
      ...state,
      itineraries: action.itineraries
    };
  }
  if (action.type === "BY_CITY_ITINERARY") {
    return {
      ...state,
      itineraries: action.itineraries
    };
  } else {
    return {
      ...state
    };
  }
};

export default rootReducer;
