import axios from "axios";

export function getActivities() {
  return dispatch => {
    axios.get(`/api/activities`).then(res => {
      dispatch(activityInfo(res.data));
    });
  };
}

export function activityInfo(activities) {
  return {
    type: "ALL_ACTIVITY",
    activities: activities
  };
}

export function getActivitiesByTitle(activities) {
  return dispatch => {
    axios.get(`/api/activities/${activities}`).then(res => {
      dispatch(activitySpecific(res.data));
      console.log(res.data);
    });
  };
}

export function activitySpecific(activities) {
  return {
    type: "ADD_SPECIFICACTIVITY",
    activities: activities
  };
}
