import React, { Component } from "react";
import axios from "axios";
// import { connect } from "react-redux";
// import { getItineraries } from "../actions/itinerariesAction";

class Itineraries extends Component {
  state = {
    itineraries: []
  };

  componentDidMount() {
    axios.get(`/api/itineraries/{city.city}`).then(res => {
      console.log(res);
      this.setState({ itineraries: res.data });
    });
  }

  render() {
    return (
      <div className="Itinerary">
        {this.state.itineraries.map(itinerary => (
          <div>
            <p className="itineraryProfilePic">{itinerary.title}</p>
            <p className="itineraryRating">{itinerary.rating}</p>
            <p className="itineraryDuration">{itinerary.profilePic}</p>
            <p className="itineraryPrice">{itinerary.price}</p>
            <p className="itineraryHashtag">{itinerary.hashtag}</p>
          </div>
        ))}
      </div>
    );
  }
}

export default Itineraries;

