import React, { Component } from "react";
import axios from "axios";

class Favourites extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUserId: "",
      favouritItineraryIds: []
    };
  }

  componentDidMount() {
    axios.get(`/api/users/${this.state.currentUserId}`).then(
      res =>
        (this.setState = {
          favouritItineraryIds: res.data.favouriteItineraries
        })
    );
  }

  componentDidMount() {
    axios
      .get(`/api/users/currentUser`, {
        withCredentials: true,
        headers: { Authorization: localStorage.getItem("token") }
      })
      .then(res => {
        console.log(res);
        console.log(res.data[0]._id);
        this.setState({ currentUserId: res.data[0]._id });
      });

    console.log(this.state.currentUserId);
  }

  componentWillReceiveProps() {
    axios
      .get(`/api/users/${this.state.currentUserId}`)
      .then(res => console.log(res.data.favouriteItineraries));
  }

  render() {
    console.log(this.state.favouritItineraryIds);

    return <div className="Favourites">my favorites page</div>;
  }
}

export default Favourites;
